import * as React from 'react';
import languages from './lanuages';

interface CodeLangProps {
  pluginState: any;
  updateCurrentPlugin: Function;
}

const CodeLang = ({ pluginState, updateCurrentPlugin }: CodeLangProps) => {
  const langSelection = (
    <select
      value={ pluginState.language }
      onChange={(e: any) => updateCurrentPlugin({ language: e.target.value })}>
        {
          languages.map((language: string, key: number) => (
            <option
              key={ key }
              value={ language } >
              { language }
            </option>
          ))
        }
    </select>
  );

  return (
    <li>
      <label className="pt-label">
        Language
        <div className="pt-select">
          { langSelection }
        </div>
      </label>
    </li>
  );
}

export default CodeLang;
