import * as React from 'react';
import { Checkbox } from '@blueprintjs/core';
import CodeEdit from './Options/CodeEdit';
import CodeHighlightSubmit from './Options/CodeHighlightSubmit';
import CodeLang from './Options/CodeLang';
import CodeRun from './Options/CodeRun';
import CodeTheme from './Options/CodeTheme';
import CodeImportExport from './Options/CodeImportExport';
import ToggleConsole from './Options/ToggleConsole';

interface OptionsMenuProps {
  moduleName: string;
  pluginNumber: number;
  pluginState: any;
  slideNumber: number;
  updateCurrentPlugin: Function;
}

const OptionsMenu = ({
  moduleName,
  pluginNumber,
  pluginState,
  slideNumber,
  updateCurrentPlugin
}: OptionsMenuProps) => (
  <ul id="codeeditor-options-container">
    <CodeLang
      pluginState={pluginState}
      updateCurrentPlugin={updateCurrentPlugin} />
    {/*
      <CodeTheme
        pluginState={ pluginState }
        updateCurrentPlugin={updateCurrentPlugin} />
    */}
    
    <hr />

    <CodeHighlightSubmit
      pluginState={pluginState}
      updateCurrentPlugin={updateCurrentPlugin} />

    <hr />

    <ul>
      <CodeImportExport
        pluginState={pluginState}
        updateCurrentPlugin={updateCurrentPlugin} />
    </ul>

    <hr />

    <Checkbox
      checked={pluginState.showLineNumbers}
      label="Show line numbers in Fullscreen"
      onChange={() => updateCurrentPlugin({ showLineNumbers: !pluginState.showLineNumbers })} />

    <hr />

    <ul style={{ margin: '10px auto 0', maxWidth: '200px' }}>
      <CodeEdit
        pluginState={pluginState}
        updateCurrentPlugin={updateCurrentPlugin} />
      <li>
        <CodeRun pluginState={pluginState} />
      </li>
      <ToggleConsole
        pluginState={pluginState}
        updateCurrentPlugin={updateCurrentPlugin} />
    </ul>
  </ul>
);

export default OptionsMenu;
