import * as React from 'react';

import FontBackgroundColor from './options/FontBackgroundColor';
import FontColor from './options/FontColor';
import FontSize from './options/FontSize';
import FontFamily from './options/FontFamily';
import FontStyles from './options/FontStyles';
import Utils from './options/Utils';

interface OptionsMenuProps {
  moduleName: string;
  pluginNumber: number;
  pluginState: any;
  slide: Object;
  slideNumber: number;
  theme: any;
  addThemeColor: Function;
  updateCurrentPlugin: Function;
}

class OptionsMenu extends React.Component<OptionsMenuProps, {}> {
  render() {
    const {
      moduleName,
      pluginNumber,
      pluginState,
      slide,
      slideNumber,
      theme,
      addThemeColor,
      updateCurrentPlugin
    } = this.props;

    return (
      <ul id="font-size-options-container" style={{ textAlign: 'center'}}>
        <li>
          <FontSize
            pluginState={ pluginState }
            updateCurrentPlugin={ updateCurrentPlugin } />
        </li>
        <li>
          <FontFamily
            pluginState={ pluginState }
            updateCurrentPlugin={ updateCurrentPlugin } />
        </li>
        <li>
          <FontStyles
            pluginState={ pluginState }
            updateCurrentPlugin={ updateCurrentPlugin } />
        </li>
        <li style={{ marginTop: 8 }}>
          <Utils
            pluginState={ pluginState }
            updateCurrentPlugin={ updateCurrentPlugin } />
        </li>
        <hr />
        <li>
          <FontColor
            pluginState={ pluginState }
            theme={ theme }
            addThemeColor={ addThemeColor }
            updateCurrentPlugin={ updateCurrentPlugin } />
        </li>
        <li>
          <FontBackgroundColor
            pluginState={ pluginState }
            slide={ slide }
            theme={ theme }
            addThemeColor={ addThemeColor }
            updateCurrentPlugin={ updateCurrentPlugin } />
        </li>
      </ul>
    );
  };
}

export default OptionsMenu;
