import * as React from 'react';
import FontColor from './FontColor';
import FontSize from './FontSize';
import FontBold from './FontBold';
import FontItalics from './FontItalics';
import FontUnderline from './FontUnderline';

interface OptionsMenuProps {
  moduleName: string;
  pluginNumber: number;
  pluginState: any;
  slideNumber: number;
  theme: any;
  addThemeColor: Function;
  updateCurrentPlugin: Function;
}

class OptionsMenu extends React.Component<OptionsMenuProps, {}> {
  render() {
    const {
      moduleName,
      pluginNumber,
      pluginState,
      slideNumber,
      theme,
      addThemeColor,
      updateCurrentPlugin
    } = this.props;

    return (
      <ul id="font-size-options-container" style={{ textAlign: 'center'}}>
        <li>
          <FontSize
            pluginState={ pluginState }
            updateCurrentPlugin={ updateCurrentPlugin } />
        </li>
        <li>
          <FontStyles
            pluginState={ pluginState }
            updateCurrentPlugin={ updateCurrentPlugin } />
        </li>
        <li>
          <FontColor
            pluginState={ pluginState }
            theme={ theme }
            addThemeColor={ addThemeColor }
            updateCurrentPlugin={ updateCurrentPlugin } />
        </li>
      </ul>
    );
  };
}

export default OptionsMenu;
