import * as React from 'react';
import { EditableText as TextBoxInput } from '@blueprintjs/core';

interface TextBoxProps {
  pluginState: any;
  updateCurrentPlugin?: any;
}

const TextBox = ({ pluginState, updateCurrentPlugin }: TextBoxProps) => {
  const { width, height, fontColor, fontSize, fontStyle, fontWeight, textDecoration, value } = pluginState;
  return (
    <div
      id="devdecks-textbox-container"
      style={{
        color: fontColor,
        fontSize: `${fontSize/100}em`,
        fontWeight: fontWeight ? 'bold' : 'normal',
        fontStyle : fontStyle  ? 'italic' : 'normal',
        textDecoration : textDecoration  ? 'underline' : 'none'
      }}>
      <TextBoxInput
        multiline
        onChange={ (value: string) => updateCurrentPlugin({ value }) }
        value={ value } />
    </div>
  );
}

export default TextBox;
