import * as React from 'react';
import { SketchPicker } from 'react-color';
import { Button } from '@blueprintjs/core';

interface FontColor {
  pluginState: any;
  theme: any;
  addThemeColor: Function;
  updateCurrentPlugin?: any;
}

const FontColor = ({ pluginState, theme, addThemeColor, updateCurrentPlugin }: FontColor) => {
  const { fontColor, fontColorHex } = pluginState;
  const { colors } = theme;
  return (
    <div style={ { marginTop: '10px'} }>
      <SketchPicker
        width='220px'
        color={ fontColor || '#000' }
        presetColors={ colors }
        onChange={ (color) =>
          updateCurrentPlugin({
            fontColor: `rgba(${color.rgb.r}, ${color.rgb.g}, ${color.rgb.b}, ${color.rgb.a})`,
            fontColorHex: color.hex
        })} />
      <Button
        style={{ marginTop: 10 }}
        text="Save Color To Palette"
        onClick={() => addThemeColor(fontColorHex || '#fff')} />
      </div>
  );
};

export default FontColor;
