import * as React from 'react';
import '../fonts/_index.scss';

interface FontFamilyProps {
  pluginState: any;
  updateCurrentPlugin: Function;
}

const FontFamily = ({ pluginState, updateCurrentPlugin }: FontFamilyProps) => {
  const { FontFamily } = pluginState;

  const fonts = [
    'Arial', 'Coda', 'Courier New', 'Droid Sans', 'Electrolize', 'Exo',
    'Hind', 'Lobster', 'Orbitron', 'Oxygen', 'Raleway', 'Roboto',
    'Satisfy', 'Tangerine', 'Times New Roman', 'Ubuntu'];

  const fontFamilySelection = (
    <select
      value={FontFamily}
      onChange={(e: any) => updateCurrentPlugin({ fontFamily: e.target.value })}>
      {
        fonts.map((fontFamily, key) => (
          <option
            key={key}
            value={fontFamily} >
            {fontFamily}
          </option>
        ))
      }
    </select>
  );

  return (
    <label className="pt-label">
      Font Family
      <div className="pt-select">
        {fontFamilySelection}
      </div>
    </label>
  );
}

export default FontFamily;
