import * as React from 'react';
import { Button, Intent } from "@blueprintjs/core";

interface FontStylesProps {
  pluginState: any;
  updateCurrentPlugin: any;
}

const FontStyles = ({ pluginState, updateCurrentPlugin }: FontStylesProps) => {
  const {
    fontStyle,
    fontWeight,
    textAlignment,
    textDecoration,
  } = pluginState;

  return (
    <div>
      <Button
        iconName="bold"
        intent={ fontWeight ? Intent.SUCCESS : Intent.NONE }
        onClick={() => updateCurrentPlugin({ fontWeight: !fontWeight })} />
      <Button
        iconName="italic"
        intent={ fontStyle ? Intent.SUCCESS : Intent.NONE }
        onClick={() => updateCurrentPlugin({ fontStyle: !fontStyle })} />
      <Button
        iconName="underline"
        intent={ textDecoration ? Intent.SUCCESS : Intent.NONE }
        onClick={() => updateCurrentPlugin({ textDecoration: !textDecoration })} />
      <span style={{ marginRight: 5 }} />
      <Button
        iconName="align-left"
        intent={ textAlignment === 'left' ? Intent.SUCCESS : Intent.NONE }
        onClick={() =>
          textAlignment === 'left'
            ? updateCurrentPlugin({ textAlignment: null })
            : updateCurrentPlugin({ textAlignment: 'left' })} />
      <Button
        iconName="align-center"
        intent={ textAlignment === 'center' ? Intent.SUCCESS : Intent.NONE }
        onClick={() =>
          textAlignment === 'center'
            ? updateCurrentPlugin({ textAlignment: null })
            : updateCurrentPlugin({ textAlignment: 'center' })} />
      <Button
        iconName="align-right"
        intent={ textAlignment === 'right' ? Intent.SUCCESS : Intent.NONE }
        onClick={() =>
          textAlignment === 'right'
            ? updateCurrentPlugin({ textAlignment: null })
            : updateCurrentPlugin({ textAlignment: 'right' })} />
    </div>
  );
};

export default FontStyles;
