import * as React from 'react';
import { Button, Intent } from "@blueprintjs/core";

interface UtilsProps {
  pluginState: any;
  updateCurrentPlugin: any;
}

const Utils = ({ pluginState, updateCurrentPlugin }: UtilsProps) => {
  const { isOrderedList, isUnorderedList } = pluginState;
  return (
    <div>
      <Button
        iconName="numbered-list"
        intent={ document.queryCommandState('insertOrderedList') || isOrderedList ? Intent.SUCCESS : Intent.NONE }
        onClick={() => {
          document.execCommand('insertOrderedList');
          updateCurrentPlugin({
            isOrderedList: document.queryCommandState('insertOrderedList'),
            isUnorderedList: null,
            textAlignment: null,
          });
        }} />
      <span style={{ marginRight: 5 }} />
      <Button
        iconName="properties"
        intent={ document.queryCommandState('insertUnorderedList') || isUnorderedList ? Intent.SUCCESS : Intent.NONE }
        onClick={() => {
          document.execCommand('insertUnorderedList');
          updateCurrentPlugin({
            isUnorderedList: document.queryCommandState('insertUnorderedList'),
            isOrderedList: null,
            textAlignment: null,
          });
        }} />
    </div>
  );
};

export default Utils;
